create table imagine_if_question
(
    id   bigserial    not null,
    question varchar not null,
    answer1 varchar(300) not null,
    answer2 varchar(300) not null,
    answer3 varchar(300) not null,
    answer4 varchar(300) not null,
    answer5 varchar(300) not null,
    answer6 varchar(300) not null,

    primary key (id)
);

create table imagine_if_game_round
(
  id                            bigserial    not null,
    game_room_id bigint not null references game_room(id),
    player_id bigint not null references player(id),
    round int not null,
    answer int,
    question_id bigint not null references imagine_if_question(id),
    selected_player_id integer default 1,
primary key (id)
);

